/** 
 * This method calculates maximum size of both width and height of bitmap. It is twice the device screen diagonal for default implementation (extra quality to zoom image). Size cannot exceed max texture size.
 * @return - max bitmap size in pixels.
 */
@SuppressWarnings({"SuspiciousNameCombination","deprecation"}) public static int calculateMaxBitmapSize(@NonNull Context context){
  WindowManager wm=(WindowManager)context.getSystemService(Context.WINDOW_SERVICE);
  Display display;
  int width, height;
  Point size=new Point();
  if (wm != null) {
    display=wm.getDefaultDisplay();
    display.getSize(size);
  }
  width=size.x;
  height=size.y;
  int maxBitmapSize=(int)Math.sqrt(Math.pow(width,2) + Math.pow(height,2));
  Canvas canvas=new Canvas();
  final int maxCanvasSize=Math.min(canvas.getMaximumBitmapWidth(),canvas.getMaximumBitmapHeight());
  if (maxCanvasSize > 0) {
    maxBitmapSize=Math.min(maxBitmapSize,maxCanvasSize);
  }
  final int maxTextureSize=EglUtils.getMaxTextureSize();
  if (maxTextureSize > 0) {
    maxBitmapSize=Math.min(maxBitmapSize,maxTextureSize);
  }
  Log.d(TAG,"maxBitmapSize: " + maxBitmapSize);
  return maxBitmapSize;
}
