/** 
 * Resolves the given package name with respect to the offset given in the constructor.
 * @param pkg the relative package name to be resolved
 * @return the absolute package name
 * @throws InvalidPackageNameException if the package name cannot be resolved(only syntactic checks are done -- it is not checked if the package really exists or not)
 */
public String resolve(String pkg) throws InvalidPackageNameException {
  boolean isAbsolute;
  String relativePkg;
  if (pkg.startsWith("//")) {
    isAbsolute=true;
    relativePkg=pkg.substring(2);
  }
 else   if (pkg.startsWith("/")) {
    throw new InvalidPackageNameException(PackageIdentifier.createInMainRepo(pkg),"package name cannot start with a single slash");
  }
 else {
    isAbsolute=false;
    relativePkg=pkg;
  }
  PathFragment relative=PathFragment.create(relativePkg);
  if (discardBuild && relative.getBaseName().equals("BUILD")) {
    relative=relative.getParentDirectory();
  }
  PathFragment result=isAbsolute ? relative : offset.getRelative(relative);
  if (result.containsUplevelReferences()) {
    throw new InvalidPackageNameException(PackageIdentifier.createInMainRepo(pkg),"package name contains too many '..' segments");
  }
  return result.getPathString();
}
