      if (blockSize > 0) {
        this.decryptBlockSize=blockSize;
      }
    }
    return doFinal(data,this.decryptBlockSize < 0 ? data.length : this.decryptBlockSize);
  }
 catch (  Exception e) {
    throw new CryptoException(e);
  }
 finally {
    lock.unlock();
  }
}
