@Test public void testErrorOnSecondSend() throws IOException, InterruptedException, GeneralSecurityException, URISyntaxException {
  try (TestWebServer server=new TestWebServer(false);Connection connection=Connection.getConnectionFactory().apply(new URL(server.getEndpoint()))){
    connection.send("GET",new Request.Builder().build());
    try {
      connection.send("GET",new Request.Builder().build());
      Assert.fail("Should fail on the second send");
    }
 catch (    IllegalStateException ex) {
      Assert.assertEquals("Connection can send only one request",ex.getMessage());
    }
  }
 }
