/** 
 * Converts a map of exposed ports as strings to a list of  {@link Port}s (e.g.  {@code}{"1000/tcp":{}}} ->  {@code Port(1000, Protocol.TCP)}).
 * @param portMap the map to convert
 * @return a list of {@link Port}s
 */
@VisibleForTesting static ImmutableList<Port> portMapToList(@Nullable Map<String,Map<?,?>> portMap) throws BadContainerConfigurationFormatException {
  if (portMap == null) {
    return ImmutableList.of();
  }
  ImmutableList.Builder<Port> ports=new ImmutableList.Builder<>();
  for (  Map.Entry<String,Map<?,?>> entry : portMap.entrySet()) {
    String port=entry.getKey();
    Matcher matcher=PORT_PATTERN.matcher(port);
    if (!matcher.matches()) {
      throw new BadContainerConfigurationFormatException("Invalid port configuration: '" + port + "'.");
    }
    int portNumber=Integer.parseInt(matcher.group("portNum"));
    String protocol=matcher.group("protocol");
    ports.add(new Port(portNumber,Protocol.getFromString(protocol)));
  }
  return ports.build();
}
