/** 
 * Enable TLS 1.2 and 1.1 on Android Kitkat. This function is mostly taken from the documentation of OkHttpClient.Builder.sslSocketFactory(_,_). <p> If there is an error, the function will safely fall back to doing nothing and printing the error to the console. </p>
 * @param builder The HTTPClient Builder on which TLS is enabled on (will be modified in-place)
 */
private static void enableModernTLS(final OkHttpClient.Builder builder){
  try {
    TrustManagerFactory trustManagerFactory=TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
    trustManagerFactory.init((KeyStore)null);
    TrustManager[] trustManagers=trustManagerFactory.getTrustManagers();
    if (trustManagers.length != 1 || !(trustManagers[0] instanceof X509TrustManager)) {
      throw new IllegalStateException("Unexpected default trust managers:" + Arrays.toString(trustManagers));
    }
    X509TrustManager trustManager=(X509TrustManager)trustManagers[0];
    SSLSocketFactory sslSocketFactory=TLSSocketFactoryCompat.getInstance();
    builder.sslSocketFactory(sslSocketFactory,trustManager);
    List<CipherSuite> cipherSuites=new ArrayList<>();
    cipherSuites.addAll(ConnectionSpec.MODERN_TLS.cipherSuites());
    cipherSuites.add(CipherSuite.TLS_ECDHE_ECDSA_WITH_AES_128_CBC_SHA);
    cipherSuites.add(CipherSuite.TLS_ECDHE_ECDSA_WITH_AES_256_CBC_SHA);
    ConnectionSpec legacyTLS=new ConnectionSpec.Builder(ConnectionSpec.MODERN_TLS).cipherSuites(cipherSuites.toArray(new CipherSuite[0])).build();
    builder.connectionSpecs(Arrays.asList(legacyTLS,ConnectionSpec.CLEARTEXT));
  }
 catch (  KeyManagementException|NoSuchAlgorithmException|KeyStoreException e) {
    if (DEBUG) {
      e.printStackTrace();
    }
  }
}
