/** 
 * transforms  {@link LogEvent}  to {@link LogData}
 * @param appender the real {@link AbstractAppender appender}
 * @param event {@link LogEvent}
 * @return {@link LogData} with filtered trace context in order to reduce the cost on the network
 */
private LogData transform(final AbstractAppender appender,LogEvent event){
  LogTags.Builder logTags=LogTags.newBuilder().addData(KeyStringValuePair.newBuilder().setKey("level").setValue(event.getLevel().toString()).build()).addData(KeyStringValuePair.newBuilder().setKey("logger").setValue(event.getLoggerName()).build()).addData(KeyStringValuePair.newBuilder().setKey("thread").setValue(event.getThreadName()).build());
  if (!ToolkitConfig.Plugin.Toolkit.Log.TRANSMIT_FORMATTED) {
    if (event.getMessage().getParameters() != null) {
      for (int i=0; i < event.getMessage().getParameters().length; i++) {
        String value=Optional.ofNullable(event.getMessage().getParameters()[i]).orElse("null").toString();
        logTags.addData(KeyStringValuePair.newBuilder().setKey("argument." + i).setValue(value).build());
      }
    }
    if (event.getThrown() != null) {
      logTags.addData(KeyStringValuePair.newBuilder().setKey("exception").setValue(ThrowableTransformer.INSTANCE.convert2String(event.getThrown(),2048)).build());
    }
  }
  LogData.Builder builder=LogData.newBuilder().setTimestamp(event.getTimeMillis()).setService(Config.Agent.SERVICE_NAME).setServiceInstance(Config.Agent.INSTANCE_NAME).setTags(logTags.build()).setBody(LogDataBody.newBuilder().setType(LogDataBody.ContentCase.TEXT.name()).setText(TextLog.newBuilder().setText(transformLogText(appender,event)).build()).build());
  return -1 == ContextManager.getSpanId() ? builder.build() : builder.setTraceContext(TraceContext.newBuilder().setTraceId(ContextManager.getGlobalTraceId()).setSpanId(ContextManager.getSpanId()).setTraceSegmentId(ContextManager.getSegmentId()).build()).build();
}
