@Test public void testInaccessibleNoArgsConstructor(){
  Gson gson=new GsonBuilder().addReflectionAccessFilter(new ReflectionAccessFilter(){
    @Override public FilterResult check(    Class<?> rawClass){
      return FilterResult.BLOCK_INACCESSIBLE;
    }
  }
).create();
  try {
    gson.fromJson("{}",ClassWithPrivateNoArgsConstructor.class);
    fail("Expected exception; test needs to be run with Java >= 9");
  }
 catch (  JsonIOException expected) {
    assertEquals("Unable to invoke no-args constructor of class com.google.gson.functional.ReflectionAccessFilterTest$ClassWithPrivateNoArgsConstructor; " + "constructor is not accessible and ReflectionAccessFilter does not permit making it accessible. Register an " + "InstanceCreator or a TypeAdapter for this type, change the visibility of the constructor or adjust the access filter.",expected.getMessage());
  }
}
