/** 
 * Generate an HTML page that does a browser redirect via a POST.  The HTML document uses Javascript to automatically submit a FORM post when loaded. This is similar to what the SAML Post Binding does. Here's an example <pre> {@code <HTML> <HEAD> <TITLE>title</TITLE> </HEAD> <BODY Onload="document.forms[0].submit()"> <FORM METHOD="POST" ACTION="actionUrl"> <INPUT TYPE="HIDDEN" NAME="param" VALUE="value"/> <NOSCRIPT> <P>JavaScript is disabled. We strongly recommend to enable it. Click the button below to continue.</P> <INPUT TYPE="SUBMIT" VALUE="CONTINUE"/> </NOSCRIPT> </FORM> </BODY> </HTML>}</pre>
 * @param title may be null.  Just the title of the HTML document
 * @param actionUrl URL to redirect to
 * @param params must be encoded so that they can be placed in an HTML form hidden INPUT field value
 * @return
 */
public String buildHtml(String title,String actionUrl,Map<String,String> params){
  StringBuilder builder=new StringBuilder();
  builder.append("<HTML>").append("<HEAD>");
  if (title != null) {
    builder.append("<TITLE>SAML HTTP Post Binding</TITLE>");
  }
  builder.append("</HEAD>").append("<BODY Onload=\"document.forms[0].submit()\">").append("<FORM METHOD=\"POST\" ACTION=\"").append(actionUrl).append("\">");
  for (  Map.Entry<String,String> param : params.entrySet()) {
    builder.append("<INPUT TYPE=\"HIDDEN\" NAME=\"").append(param.getKey()).append("\"").append(" VALUE=\"").append(param.getValue()).append("\"/>");
  }
  builder.append("<NOSCRIPT>").append("<P>JavaScript is disabled. We strongly recommend to enable it. Click the button below to continue.</P>").append("<INPUT TYPE=\"SUBMIT\" VALUE=\"CONTINUE\" />").append("</NOSCRIPT>").append("</FORM></BODY></HTML>");
  return builder.toString();
}
