private void updateSelectedState(@Nullable TextView dayTextView,long date,int dayNumber){
  if (dayTextView == null) {
    return;
  }
  final CalendarItemStyle style;
  boolean valid=calendarConstraints.getDateValidator().isValid(date);
  boolean selected=false;
  if (valid) {
    dayTextView.setEnabled(true);
    selected=isSelected(date);
    if (selected) {
      style=calendarStyle.selectedDay;
    }
 else     if (UtcDates.getTodayCalendar().getTimeInMillis() == date) {
      style=calendarStyle.todayDay;
    }
 else {
      style=calendarStyle.day;
    }
  }
 else {
    dayTextView.setEnabled(false);
    style=calendarStyle.invalidDay;
  }
  if (dayViewDecorator != null && dayNumber != NO_DAY_NUMBER) {
    Context context=dayTextView.getContext();
    long dayInMillis=month.getDay(dayNumber);
    int year=month.year;
    int month=this.month.month;
    ColorStateList backgroundColorOverride=dayViewDecorator.getBackgroundColor(context,year,month,dayNumber,valid,selected);
    style.styleItem(dayTextView,backgroundColorOverride);
    Drawable drawableLeft=dayViewDecorator.getCompoundDrawableLeft(context,year,month,dayNumber,valid,selected);
    Drawable drawableTop=dayViewDecorator.getCompoundDrawableTop(context,year,month,dayNumber,valid,selected);
    Drawable drawableRight=dayViewDecorator.getCompoundDrawableRight(context,year,month,dayNumber,valid,selected);
    Drawable drawableBottom=dayViewDecorator.getCompoundDrawableBottom(context,year,month,dayNumber,valid,selected);
    dayTextView.setCompoundDrawables(drawableLeft,drawableTop,drawableRight,drawableBottom);
    CharSequence decoratorContentDescription=dayViewDecorator.getContentDescription(context,year,month,dayNumber,valid,selected,DateStrings.getOptionalYearMonthDayOfWeekDay(dayInMillis));
    dayTextView.setContentDescription(decoratorContentDescription);
  }
 else {
    style.styleItem(dayTextView);
  }
}
