/** 
 * Computes scale of target size (<b>targetSize</b>) to source size (<b>srcSize</b>).<br /> <br /> <b>Examples:</b><br /> <p/> <pre> srcSize(40x40), targetSize(10x10) -> scale = 0.25 srcSize(10x10), targetSize(20x20), stretch = false -> scale = 1 srcSize(10x10), targetSize(20x20), stretch = true  -> scale = 2 srcSize(100x100), targetSize(20x40), viewScaleType = FIT_INSIDE -> scale = 0.2 srcSize(100x100), targetSize(20x40), viewScaleType = CROP       -> scale = 0.4 </pre>
 * @param srcSize       Source (image) size
 * @param targetSize    Target (view) size
 * @param viewScaleType {@linkplain ViewScaleType Scale type} for placing image in view
 * @param stretch       Whether source size should be stretched if target size is larger than source size. If <b>false</b>then result scale value can't be greater than 1.
 * @return Computed scale
 */
public static float computeImageScale(ImageSize srcSize,ImageSize targetSize,ViewScaleType viewScaleType,boolean stretch){
  final int srcWidth=srcSize.getWidth();
  final int srcHeight=srcSize.getHeight();
  final int targetWidth=targetSize.getWidth();
  final int targetHeight=targetSize.getHeight();
  final float widthScale=(float)srcWidth / targetWidth;
  final float heightScale=(float)srcHeight / targetHeight;
  final int destWidth;
  final int destHeight;
  if ((viewScaleType == ViewScaleType.FIT_INSIDE && widthScale >= heightScale) || (viewScaleType == ViewScaleType.CROP && widthScale < heightScale)) {
    destWidth=targetWidth;
    destHeight=(int)(srcHeight / widthScale);
  }
 else {
    destWidth=(int)(srcWidth / heightScale);
    destHeight=targetHeight;
  }
  float scale=1;
  if ((!stretch && destWidth < srcWidth && destHeight < srcHeight) || (stretch && destWidth != srcWidth && destHeight != srcHeight)) {
    scale=(float)destWidth / srcWidth;
  }
  return scale;
}

