  }
 else   if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.HONEYCOMB && fragment instanceof Fragment) {
    Fragment f=(Fragment)fragment;
    return o.lift(new OperatorConditionalBinding<T,Fragment>(f,FRAGMENT_VALIDATOR));
