  }
 else   if (selectedCharset.equals(charsetMs)) {
    charset=CHARSET_MOST_SPECIAL;
  }
 else {
    charset=selectedCharset;
  }
  Pattern pattern=Pattern.compile(charset);
  return createFilename(title,pattern,replacementChar);
