/** 
 * Execute export config operation.
 * @param dataId  dataId string value.
 * @param group   group string value.
 * @param appName appName string value.
 * @param tenant  tenant string value.
 * @param ids     id list value.
 * @return ResponseEntity.
 */
@GetMapping(params="export=true") @Secured(action=ActionTypes.READ,parser=ConfigResourceParser.class) public ResponseEntity<byte[]> exportConfig(@RequestParam(value="dataId",required=false) String dataId,@RequestParam(value="group",required=false) String group,@RequestParam(value="appName",required=false) String appName,@RequestParam(value="tenant",required=false,defaultValue=StringUtils.EMPTY) String tenant,@RequestParam(value="ids",required=false) List<Long> ids){
  ids.removeAll(Collections.singleton(null));
  tenant=NamespaceUtil.processNamespaceParameter(tenant);
  List<ConfigAllInfo> dataList=persistService.findAllConfigInfo4Export(dataId,group,tenant,appName,ids);
  List<ZipUtils.ZipItem> zipItemList=new ArrayList<>();
  StringBuilder metaData=null;
  for (  ConfigInfo ci : dataList) {
    if (StringUtils.isNotBlank(ci.getAppName())) {
      if (metaData == null) {
        metaData=new StringBuilder();
      }
      String metaDataId=ci.getDataId();
      if (metaDataId.contains(".")) {
        metaDataId=metaDataId.substring(0,metaDataId.lastIndexOf(".")) + "~" + metaDataId.substring(metaDataId.lastIndexOf(".") + 1);
      }
      metaData.append(ci.getGroup()).append(".").append(metaDataId).append(".app=").append(ci.getAppName()).append("\r\n");
    }
    String itemName=ci.getGroup() + "/" + ci.getDataId();
    zipItemList.add(new ZipUtils.ZipItem(itemName,ci.getContent()));
  }
  if (metaData != null) {
    zipItemList.add(new ZipUtils.ZipItem(".meta.yml",metaData.toString()));
  }
  HttpHeaders headers=new HttpHeaders();
  String fileName=EXPORT_CONFIG_FILE_NAME + DateFormatUtils.format(new Date(),EXPORT_CONFIG_FILE_NAME_DATE_FORMAT) + EXPORT_CONFIG_FILE_NAME_EXT;
  headers.add("Content-Disposition","attachment;filename=" + fileName);
  return new ResponseEntity<byte[]>(ZipUtils.zip(zipItemList),headers,HttpStatus.OK);
}
