  String codecs=selectedPlaylistFormats[0].codecs;
  int numberOfVideoCodecs=Util.getCodecCountOfType(codecs,C.TRACK_TYPE_VIDEO);
  int numberOfAudioCodecs=Util.getCodecCountOfType(codecs,C.TRACK_TYPE_AUDIO);
  boolean codecsStringAllowsChunklessPreparation=numberOfAudioCodecs <= 1 && numberOfVideoCodecs <= 1 && numberOfAudioCodecs + numberOfVideoCodecs > 0;
  int trackType=!useVideoVariantsOnly && numberOfAudioCodecs > 0 ? C.TRACK_TYPE_AUDIO : C.TRACK_TYPE_DEFAULT;
  HlsSampleStreamWrapper sampleStreamWrapper=buildSampleStreamWrapper(trackType,selectedPlaylistUrls,selectedPlaylistFormats,masterPlaylist.muxedAudioFormat,masterPlaylist.muxedCaptionFormats,overridingDrmInitData,positionUs);
  sampleStreamWrappers.add(sampleStreamWrapper);
  manifestUrlIndicesPerWrapper.add(selectedVariantIndices);
  if (allowChunklessPreparation && codecsStringAllowsChunklessPreparation) {
    List<TrackGroup> muxedTrackGroups=new ArrayList<>();
    if (numberOfVideoCodecs > 0) {
      Format[] videoFormats=new Format[selectedVariantsCount];
      for (int i=0; i < videoFormats.length; i++) {
        videoFormats[i]=deriveVideoFormat(selectedPlaylistFormats[i]);
      }
      muxedTrackGroups.add(new TrackGroup(videoFormats));
      if (numberOfAudioCodecs > 0 && (masterPlaylist.muxedAudioFormat != null || masterPlaylist.audios.isEmpty())) {
        muxedTrackGroups.add(new TrackGroup(deriveAudioFormat(selectedPlaylistFormats[0],masterPlaylist.muxedAudioFormat,false)));
      }
      List<Format> ccFormats=masterPlaylist.muxedCaptionFormats;
      if (ccFormats != null) {
        for (int i=0; i < ccFormats.size(); i++) {
          muxedTrackGroups.add(new TrackGroup(ccFormats.get(i)));
        }
      }
    }
 else {
      Format[] audioFormats=new Format[selectedVariantsCount];
      for (int i=0; i < audioFormats.length; i++) {
        audioFormats[i]=deriveAudioFormat(selectedPlaylistFormats[i],masterPlaylist.muxedAudioFormat,true);
      }
      muxedTrackGroups.add(new TrackGroup(audioFormats));
    }
    TrackGroup id3TrackGroup=new TrackGroup(new Format.Builder().setId("ID3").setSampleMimeType(MimeTypes.APPLICATION_ID3).build());
    muxedTrackGroups.add(id3TrackGroup);
    sampleStreamWrapper.prepareWithMasterPlaylistInfo(muxedTrackGroups.toArray(new TrackGroup[0]),0,muxedTrackGroups.indexOf(id3TrackGroup));
  }
}

/** 
 * This method creates and starts preparation of the main  {@link HlsSampleStreamWrapper}. <p>The main sample stream wrapper is the first element of  {@link #sampleStreamWrappers}. It provides  {@link SampleStream}s for the variant urls in the master playlist. It may be adaptive and may contain multiple muxed tracks. <p>If chunkless preparation is allowed, the media period will try preparation without segment downloads. This is only possible if variants contain the CODECS attribute. If not, traditional preparation with segment downloads will take place. The following points apply to chunkless preparation: <ul> <li>A muxed audio track will be exposed if the codecs list contain an audio entry and the master playlist either contains an EXT-X-MEDIA tag without the URI attribute or does not contain any EXT-X-MEDIA tag. <li>Closed captions will only be exposed if they are declared by the master playlist. <li>An ID3 track is exposed preemptively, in case the segments contain an ID3 track. </ul>
 * @param masterPlaylist The HLS master playlist.
 * @param positionUs If preparation requires any chunk downloads, the position in microseconds atwhich downloading should start. Ignored otherwise.
 * @param sampleStreamWrappers List to which the built main sample stream wrapper should be added.
 * @param manifestUrlIndicesPerWrapper List to which the selected variant indices should be added.
 * @param overridingDrmInitData Overriding {@link DrmInitData}, keyed by protection scheme type (i.e.  {@link DrmInitData#schemeType}).
 */
private void buildAndPrepareMainSampleStreamWrapper(HlsMasterPlaylist masterPlaylist,long positionUs,List<HlsSampleStreamWrapper> sampleStreamWrappers,List<int[]> manifestUrlIndicesPerWrapper,Map<String,DrmInitData> overridingDrmInitData){
  int[] variantTypes=new int[masterPlaylist.variants.size()];
  int videoVariantCount=0;
  int audioVariantCount=0;
  for (int i=0; i < masterPlaylist.variants.size(); i++) {
    Variant variant=masterPlaylist.variants.get(i);
    Format format=variant.format;
    if (format.height > 0 || Util.getCodecsOfType(format.codecs,C.TRACK_TYPE_VIDEO) != null) {
      variantTypes[i]=C.TRACK_TYPE_VIDEO;
      videoVariantCount++;
    }
 else     if (Util.getCodecsOfType(format.codecs,C.TRACK_TYPE_AUDIO) != null) {
      variantTypes[i]=C.TRACK_TYPE_AUDIO;
      audioVariantCount++;
    }
 else {
      variantTypes[i]=C.TRACK_TYPE_UNKNOWN;
    }
  }
  boolean useVideoVariantsOnly=false;
  boolean useNonAudioVariantsOnly=false;
  int selectedVariantsCount=variantTypes.length;
  if (videoVariantCount > 0) {
    useVideoVariantsOnly=true;
    selectedVariantsCount=videoVariantCount;
  }
 else   if (audioVariantCount < variantTypes.length) {
    useNonAudioVariantsOnly=true;
    selectedVariantsCount=variantTypes.length - audioVariantCount;
  }
  Uri[] selectedPlaylistUrls=new Uri[selectedVariantsCount];
  Format[] selectedPlaylistFormats=new Format[selectedVariantsCount];
  int[] selectedVariantIndices=new int[selectedVariantsCount];
  int outIndex=0;
  for (int i=0; i < masterPlaylist.variants.size(); i++) {
    if ((!useVideoVariantsOnly || variantTypes[i] == C.TRACK_TYPE_VIDEO) && (!useNonAudioVariantsOnly || variantTypes[i] != C.TRACK_TYPE_AUDIO)) {
      Variant variant=masterPlaylist.variants.get(i);
      selectedPlaylistUrls[outIndex]=variant.url;
      selectedPlaylistFormats[outIndex]=variant.format;
      selectedVariantIndices[outIndex++]=i;
    }
  }
  String codecs=selectedPlaylistFormats[0].codecs;
  int numberOfVideoCodecs=Util.getCodecCountOfType(codecs,C.TRACK_TYPE_VIDEO);
  int numberOfAudioCodecs=Util.getCodecCountOfType(codecs,C.TRACK_TYPE_AUDIO);
  boolean codecsStringAllowsChunklessPreparation=numberOfAudioCodecs <= 1 && numberOfVideoCodecs <= 1 && numberOfAudioCodecs + numberOfVideoCodecs > 0;
  int trackType=!useVideoVariantsOnly && numberOfAudioCodecs > 0 ? C.TRACK_TYPE_AUDIO : C.TRACK_TYPE_DEFAULT;
  HlsSampleStreamWrapper sampleStreamWrapper=buildSampleStreamWrapper(trackType,selectedPlaylistUrls,selectedPlaylistFormats,masterPlaylist.muxedAudioFormat,masterPlaylist.muxedCaptionFormats,overridingDrmInitData,positionUs);
  sampleStreamWrappers.add(sampleStreamWrapper);
  manifestUrlIndicesPerWrapper.add(selectedVariantIndices);
  if (allowChunklessPreparation && codecsStringAllowsChunklessPreparation) {
    List<TrackGroup> muxedTrackGroups=new ArrayList<>();
    if (numberOfVideoCodecs > 0) {
      Format[] videoFormats=new Format[selectedVariantsCount];
      for (int i=0; i < videoFormats.length; i++) {
        videoFormats[i]=deriveVideoFormat(selectedPlaylistFormats[i]);
      }
      muxedTrackGroups.add(new TrackGroup(videoFormats));
      if (numberOfAudioCodecs > 0 && (masterPlaylist.muxedAudioFormat != null || masterPlaylist.audios.isEmpty())) {
        muxedTrackGroups.add(new TrackGroup(deriveAudioFormat(selectedPlaylistFormats[0],masterPlaylist.muxedAudioFormat,false)));
      }
      List<Format> ccFormats=masterPlaylist.muxedCaptionFormats;
      if (ccFormats != null) {
        for (int i=0; i < ccFormats.size(); i++) {
          muxedTrackGroups.add(new TrackGroup(ccFormats.get(i)));
        }
      }
    }
 else {
      Format[] audioFormats=new Format[selectedVariantsCount];
      for (int i=0; i < audioFormats.length; i++) {
        audioFormats[i]=deriveAudioFormat(selectedPlaylistFormats[i],masterPlaylist.muxedAudioFormat,true);
      }
      muxedTrackGroups.add(new TrackGroup(audioFormats));
    }
    TrackGroup id3TrackGroup=new TrackGroup(new Format.Builder().setId("ID3").setSampleMimeType(MimeTypes.APPLICATION_ID3).build());
    muxedTrackGroups.add(id3TrackGroup);
    sampleStreamWrapper.prepareWithMasterPlaylistInfo(muxedTrackGroups.toArray(new TrackGroup[0]),0,muxedTrackGroups.indexOf(id3TrackGroup));
  }
}
