  Map<Point,String> original=new LinkedHashMap<Point,String>();
  original.put(new Point(5,5),"a");
  original.put(new Point(8,8),"b");
  String json=gson.toJson(original,type);
  assertEquals("[[{\"x\":5,\"y\":5},\"a\"],[{\"x\":8,\"y\":8},\"b\"]]",json);
  assertEquals(original,gson.<Map<Point,String>>fromJson(json,type));
  Map<String,Boolean> otherMap=new LinkedHashMap<String,Boolean>();
  otherMap.put("t",true);
  otherMap.put("f",false);
  assertEquals("{\"t\":true,\"f\":false}",gson.toJson(otherMap,Map.class));
  assertEquals("{\"t\":true,\"f\":false}",gson.toJson(otherMap,new TypeToken<Map<String,Boolean>>(){
  }
.getType()));
  assertEquals(otherMap,gson.<Object>fromJson("{\"t\":true,\"f\":false}",new TypeToken<Map<String,Boolean>>(){
  }
.getType()));
}
