/** 
 * Dummy Espresso action that waits for at least the given amount of milliseconds. This action can be performed on the root view to wait for an ongoing animation to be completed.
 */
public static ViewAction waitFor(final long ms){
  return new ViewAction(){
    @Override public Matcher<View> getConstraints(){
      return isRoot();
    }
    @Override public String getDescription(){
      return "wait for " + ms + " ms";
    }
    @Override public void perform(    UiController uiController,    View view){
      uiController.loopMainThreadForAtLeast(ms);
    }
  }
;
}

