void show(@Nullable final InternalVisibilityChangedListener listener,final boolean fromUser){
  if (isOrWillBeShown()) {
    return;
  }
  if (currentAnimator != null) {
    currentAnimator.cancel();
  }
  if (shouldAnimateVisibilityChange()) {
    if (view.getVisibility() != View.VISIBLE) {
      view.setAlpha(0f);
      view.setScaleY(0f);
      view.setScaleX(0f);
      setImageMatrixScale(0f);
    }
    AnimatorSet set=createAnimator(showMotionSpec != null ? showMotionSpec : getDefaultShowMotionSpec(),SHOW_OPACITY,SHOW_SCALE,SHOW_ICON_SCALE);
    set.addListener(new AnimatorListenerAdapter(){
      @Override public void onAnimationStart(      Animator animation){
        view.internalSetVisibility(View.VISIBLE,fromUser);
        animState=ANIM_STATE_SHOWING;
        currentAnimator=animation;
      }
      @Override public void onAnimationEnd(      Animator animation){
        animState=ANIM_STATE_NONE;
        currentAnimator=null;
        if (listener != null) {
          listener.onShown();
        }
      }
    }
);
    if (showListeners != null) {
      for (      AnimatorListener l : showListeners) {
        set.addListener(l);
      }
    }
    set.start();
  }
 else {
    view.internalSetVisibility(View.VISIBLE,fromUser);
    view.setAlpha(1f);
    view.setScaleY(1f);
    view.setScaleX(1f);
    setImageMatrixScale(1f);
    if (listener != null) {
      listener.onShown();
    }
  }
}

