addArg(code,insn.getArg(0));
code.add(" == ");
addArg(code,insn.getArg(1));
code.add(" ? 0 : -1))");
break;
case INSTANCE_OF:{
boolean wrap=state.contains(Flags.BODY_ONLY);
if (wrap) {
code.add('(');
}
addArg(code,insn.getArg(0));
code.add(" instanceof ");
useType(code,(ArgType)((IndexInsnNode)insn).getIndex());
if (wrap) {
code.add(')');
}
break;
}
case CONSTRUCTOR:makeConstructor((ConstructorInsn)insn,code);
break;
case INVOKE:makeInvoke((InvokeNode)insn,code);
break;
case NEW_ARRAY:{
ArgType arrayType=((NewArrayNode)insn).getArrayType();
code.add("new ");
useType(code,arrayType.getArrayRootElement());
code.add('[');
addArg(code,insn.getArg(0));
code.add(']');
int dim=arrayType.getArrayDimension();
for (int i=0; i < dim - 1; i++) {
code.add("[]");
}
break;
}
case ARRAY_LENGTH:addArg(code,insn.getArg(0));
code.add(".length");
break;
case FILLED_NEW_ARRAY:filledNewArray((FilledNewArrayNode)insn,code);
break;
case AGET:addArg(code,insn.getArg(0));
code.add('[');
addArg(code,insn.getArg(1),false);
code.add(']');
break;
case APUT:addArg(code,insn.getArg(0));
code.add('[');
addArg(code,insn.getArg(1),false);
code.add("] = ");
addArg(code,insn.getArg(2),false);
break;
case IGET:{
FieldInfo fieldInfo=(FieldInfo)((IndexInsnNode)insn).getIndex();
instanceField(code,fieldInfo,insn.getArg(0));
break;
}
case IPUT:{
FieldInfo fieldInfo=(FieldInfo)((IndexInsnNode)insn).getIndex();
instanceField(code,fieldInfo,insn.getArg(1));
code.add(" = ");
addArg(code,insn.getArg(0),false);
break;
}
case SGET:staticField(code,(FieldInfo)((IndexInsnNode)insn).getIndex());
break;
case SPUT:FieldInfo field=(FieldInfo)((IndexInsnNode)insn).getIndex();
staticField(code,field);
code.add(" = ");
addArg(code,insn.getArg(0),false);
break;
case STR_CONCAT:boolean wrap=state.contains(Flags.BODY_ONLY);
if (wrap) {
code.add('(');
}
for (Iterator<InsnArg> it=insn.getArguments().iterator(); it.hasNext(); ) {
addArg(code,it.next());
if (it.hasNext()) {
code.add(" + ");
}
}
if (wrap) {
code.add(')');
}
break;
case MONITOR_ENTER:if (isFallback()) {
code.add("monitor-enter(");
addArg(code,insn.getArg(0));
code.add(')');
}
break;
case MONITOR_EXIT:if (isFallback()) {
code.add("monitor-exit(");
addArg(code,insn.getArg(0));
code.add(')');
}
break;
case TERNARY:makeTernary((TernaryInsn)insn,code,state);
break;
case ONE_ARG:addArg(code,insn.getArg(0));
break;
case IF:fallbackOnlyInsn(insn);
IfNode ifInsn=(IfNode)insn;
code.add("if (");
addArg(code,insn.getArg(0));
code.add(' ');
code.add(ifInsn.getOp().getSymbol()).add(' ');
addArg(code,insn.getArg(1));
code.add(") goto ").add(MethodGen.getLabelName(ifInsn.getTarget()));
break;
case GOTO:fallbackOnlyInsn(insn);
code.add("goto ").add(MethodGen.getLabelName(((GotoNode)insn).getTarget()));
break;
case MOVE_EXCEPTION:fallbackOnlyInsn(insn);
code.add("move-exception");
break;
case SWITCH:fallbackOnlyInsn(insn);
SwitchNode sw=(SwitchNode)insn;
code.add("switch(");
addArg(code,insn.getArg(0));
code.add(") {");
code.incIndent();
for (int i=0; i < sw.getCasesCount(); i++) {
String key=sw.getKeys()[i].toString();
code.startLine("case ").add(key).add(": goto ");
code.add(MethodGen.getLabelName(sw.getTargets()[i])).add(';');
}
code.startLine("default: goto ");
code.add(MethodGen.getLabelName(sw.getDefaultCaseOffset())).add(';');
code.decIndent();
code.startLine('}');
break;
case FILL_ARRAY:fallbackOnlyInsn(insn);
FillArrayNode arrayNode=(FillArrayNode)insn;
Object data=arrayNode.getData();
String arrStr;
if (data instanceof int[]) {
arrStr=Arrays.toString((int[])data);
}
 else if (data instanceof short[]) {
arrStr=Arrays.toString((short[])data);
}
 else if (data instanceof byte[]) {
arrStr=Arrays.toString((byte[])data);
}
 else if (data instanceof long[]) {
arrStr=Arrays.toString((long[])data);
}
 else {
arrStr="?";
}
code.add('{').add(arrStr.substring(1,arrStr.length() - 1)).add('}');
break;
case NEW_INSTANCE:fallbackOnlyInsn(insn);
code.add("new ").add(insn.getResult().getType().toString());
break;
case PHI:case MERGE:fallbackOnlyInsn(insn);
code.add(insn.getType().toString()).add('(');
for (InsnArg insnArg : insn.getArguments()) {
addArg(code,insnArg);
code.add(' ');
}
code.add(')');
break;
default:throw new CodegenException(mth,"Unknown instruction: " + insn.getType());
}
}

private void makeInsnBody(CodeWriter code,InsnNode insn,Set<Flags> state) throws CodegenException {
switch (insn.getType()) {
case CONST_STR:    String str=((ConstStringNode)insn).getString();
  code.add(mth.dex().root().getStringUtils().unescapeString(str));
break;
case CONST_CLASS:ArgType clsType=((ConstClassNode)insn).getClsType();
useType(code,clsType);
code.add(".class");
break;
case CONST:LiteralArg arg=(LiteralArg)insn.getArg(0);
code.add(lit(arg));
break;
case MOVE:addArg(code,insn.getArg(0),false);
break;
case CHECK_CAST:case CAST:{
boolean wrap=state.contains(Flags.BODY_ONLY);
if (wrap) {
code.add('(');
}
code.add('(');
useType(code,(ArgType)((IndexInsnNode)insn).getIndex());
code.add(") ");
addArg(code,insn.getArg(0),true);
if (wrap) {
code.add(')');
}
break;
}
case ARITH:makeArith((ArithNode)insn,code,state);
break;
case NEG:oneArgInsn(code,insn,state,'-');
break;
case NOT:char op=insn.getArg(0).getType() == ArgType.BOOLEAN ? '!' : '~';
oneArgInsn(code,insn,state,op);
break;
case RETURN:if (insn.getArgsCount() != 0) {
code.add("return ");
addArg(code,insn.getArg(0),false);
}
 else {
